```c
#include <stdio.h>
#include <math.h> // For the fabs function

float my_sqrt(float number, float precision);

int main() {
    float number, precision, result;

    // Example usage
    number = 25.0;
    precision = 0.0001;
    result = my_sqrt(number, precision);
    printf("The square root of %.4f is approximately %.4f\n", number, result);

    return 0;
}

float my_sqrt(float number, float precision) {
    float guess = number / 2.0; // Start with an initial guess

    while (fabs(guess * guess - number) >= precision) {
        guess = (guess + number / guess) / 2.0; // Improve the guess using the Babylonian method
    }

    return guess;
}
```

