#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int size;
    int capacity;
    int* arr;
} Set;

Set** rref(int** matrix, int rows, int cols, int graphlen) {
    const int PH = 16;

    int extra = 0;
    int col = 0;
    int* pivots = malloc(rows * sizeof(int));

    Set** used_for_row = malloc(rows * sizeof(Set*));
    for (int i = 0; i < rows; ++i) {
        used_for_row[i] = malloc(sizeof(Set));
        used_for_row[i]->size = 1;
        used_for_row[i]->capacity = 1;
        used_for_row[i]->arr = malloc(sizeof(int));
        used_for_row[i]->arr[0] = i;
    }

    while (col + extra < cols - 1 && col < rows) {
        if (matrix[col][col + extra] == 0) {
            int found_nonzero = 0;
            for (int i = 0; i < rows; ++i) {
                if (matrix[i][col + extra] != 0) {
                    found_nonzero = 1;
                    int other = i;
                    if (other < col) {
                        extra++;
                        break;
                    }

                    // Swap rows
                    int* temp_row = matrix[col];
                    matrix[col] = matrix[other];
                    matrix[other] = temp_row;

                    // Swap used_for_row sets
                    Set* temp_set = used_for_row[col];
                    used_for_row[col] = used_for_row[other];
                    used_for_row[other] = temp_set;

                    break;
                }
            }
            if (!found_nonzero) {
                extra++;
                continue;
            }
        }

        pivots[col] = col + extra;
        int pivot = matrix[col][col + extra];
        if (col + extra < graphlen) {
            if (pivot != 1 && pivot != PH - 1) {
                printf("Invalid pivot value: %d\n", pivot);
                exit(1);
            }
        } else {
            if (pivot != 2 && pivot != PH - 2) {
                printf("Invalid pivot value: %d\n", pivot);
                exit(1);
            }
            pivot /= 2;
        }
        for (int i = 0; i < cols; ++i) {
            matrix[col][i] = (matrix[col][i] * pivot) % PH;
        }

        for (int i = 0; i < rows; ++i) {
            if (i == col || matrix[i][col + extra] == 0) continue;

            // Merge sets
            for (int j = 0; j < used_for_row[col]->size; ++j) {
